document.addEventListener("DOMContentLoaded",()=>{const t=document.querySelector("[data-copy-link]");!t||!navigator.clipboard||t.addEventListener("click",async()=>{try{await navigator.clipboard.writeText(window.location.href);const e=t.textContent;t.textContent="Link copied!",t.disabled=!0,setTimeout(()=>{t.textContent=e,t.disabled=!1},2e3)}catch(e){console.error(e),alert("Could not copy the link. Please copy it manually.")}})});
